public class OpEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int test;
		test = 10/8;
		System.out.println(test);
		test = 10/-8;
		System.out.println(test);
		test = -10/8;
		System.out.println(test);
		test = -10/-8;
		System.out.println(test);
		test = 10%8;
		System.out.println(test);
		test = 10%-8;
		System.out.println(test);
		test = -10%8;
		System.out.println(test);
		test = -10%-8;
		System.out.println(test);
	}
}
